ub.execute_input":"2022-02-22T14:26:52.157136Z","iopub.status.busy":"2022-02-22T14:26:52.154657Z","iopub.status.idle":"2022-02-22T14:26:52.191026Z","shell.execute_reply":"2022-02-22T14:26:52.189266Z"},"papermill":{"duration":0.081412,"end_time":"2022-02-22T14:26:52.191212","exception":false,"start_time":"2022-02-22T14:26:52.1098","status":"completed"},"tags":[]},"outputs":[],"source":["## 2019 ASR\n","ASR_2019 <- subset(EC,EC$year==2019 & \n","                     EC$age=='Age-standardized' & \n","                     EC$metric== 'Rate' &\n","                     EC$measure=='Incidence')\n","\n","ASR_2019 <- ASR_2019[,c(2,8,9,10)]  ### ֻȡ��Ҫ�ı����������Լ���Ӧ����ֵ\n","ASR_2019$val <- round(ASR_2019$val,1)  ###ȡ��\n","ASR_2019$lower <- round(ASR_2019$lower,1)###ȡ��\n","ASR_2019$upper <- round(ASR_2019$upper,1) ###ȡ��\n","ASR_2019$ASR_2019 <- paste(ASR_2019$lower,ASR_2019$upper,sep = '-') ## ��-����95%UI������ֵ\n","ASR_2019$ASR_2019 <- paste(ASR_2019$ASR_2019,')',sep = '')  ##95%UIǰ�������             \n","ASR_2019$ASR_2019 <- paste('(',ASR_2019$ASR_2019,sep = '')  ##95%UIǰ�������\n","ASR_2019$ASR_2019 <- paste(ASR_2019$val,ASR_2019$ASR_2019,sep = ' ') ##���ݺ�95%UI�ÿո������\n","\n","EC_incidence <- subset(EC, EC$age_name=='Age-standardized' & \n","                         EC$metric_name== 'Rate' &\n","                         EC$measure_name=='Incidence')"]},{"cell_type":"code","execution_count":8,"id":"82600245","metadata":{"execution":{"iopub.execute_input":"2022-02-22T14:26:52.279722Z","iopub.status.busy":"2022-02-22T14:26:52.27748Z","iopub.status.idle":"2022-02-22T14:26:52.403207Z","shell.execute_reply":"2022-02-22T14:26:52.401692Z"},"papermill":{"duration":0.171069,"end_time":"2022-02-22T14:26:52.403373","exception":false,"start_time":"2022-02-22T14:26:52.232304","status":"completed"},"tags":[]},"outputs":[],"source":["##### EAPC\n","EAPC <- subset(EC, EC$age=='Age-standardized' & \n","                 EC$metric== 'Rate' &\n","                 EC$measure=='Incidence')\n","EAPC <- EAPC[,c(2,7,8)] ##��ȡ����������Լ���Ӧ����ֵ\n","\n","\n","\n","country <- EC_1990$location\n","EAPC_cal <- data.frame(location=country,EAPC=rep(0,times=22),UCI=rep(0,times=22),LCI=rep(0,times=22)) \n","for (i in 1:22){  ###�ܹ�22������������ѭ��22��\n","  country_cal <- as.character(EAPC_cal[i,1]) ### ����ȡ��Ӧ�ĵ���\n","  a <- subset(EAPC, EAPC$location==country_cal)  ##ȡ��Ӧ�����������Ӽ�\n","  a$y <- log(a$val)  ##����EAPC���㷽������yֵ\n","  mod_simp_reg<-lm(y~year,data=a) ##����EAPC���㷽�������Իع鷽��\n","  estimate <- (exp(summary(mod_simp_reg)[[\"coefficients\"]][2,1])-1)*100 ##����EAPC���㷽��ȡ����betaֵ������EAPC\n","  low <- (exp(summary(mod_simp_reg)[[\"coefficients\"]][2,1]-1.96*summary(mod_simp_reg)[[\"coefficients\"]][2,2])-1)*100\n","  ### ����EAPC��95%�������������ֵ\n","  high <- (exp(summary(mod_simp_reg)[[\"coefficients\"]][2,1]+1.96*summary(mod_simp_reg)[[\"coefficients\"]][2,2])-1)*100\n","  ### ����EAPC��95%�������������ֵ\n","  EAPC_cal[i,2] <- estimate\n","  EAPC_cal[i,4] <- low\n","  EAPC_cal[i,3] <- high\n","}\n","\n","EAPC_cal$EAPC <- round(EAPC_cal$EAPC,2)  ##����2λС����\n","EAPC_cal$UCI <- round(EAPC_cal$UCI,2)\n","EAPC_cal$LCI <- round(EAPC_cal$LCI,2)\n","EAPC_cal$EAPC_CI <- paste(EAPC_cal$LCI,EAPC_cal$UCI,sep = '-') \n","EAPC_cal$EAPC_CI <- paste(EAPC_cal$EAPC_CI,')',sep = '') \n","EAPC_cal$EAPC_CI <- paste('(',EAPC_cal$EAPC_CI,sep = '') \n","EAPC_cal$EAPC_CI <- paste(EAPC_cal$EAPC,EAPC_cal$EAPC_CI,sep = ' ') "]},{"cell_type":"code","execution_count":9,"id":"b5e20fcb","metadata":{"execution":{"iopub.execute_input":"2022-02-22T14:26:52.490503Z","iopub.status.busy":"2022-02-22T14:26:52.488835Z","iopub.status.idle":"2022-02-22T14:26:52.56584Z","shell.execute_reply":"2022-02-22T14:26:52.532333Z"},"papermill":{"duration":0.121786,"end_time":"2022-02-22T14:26:52.566013","exception":false,"start_time":"2022-02-22T14:26:52.444227","status":"completed"},"tags":[]},"outputs":[{"data":{"text/html":["<table class=\"dataframe\">\n","<caption>A data.